import{_ as o,c as d,o as c,ae as a}from"./chunks/framework.Cqi9LMMK.js";const b=JSON.parse('{"title":"卡牌与活动信息查询","description":"","frontmatter":{},"headers":[],"relativePath":"src/harukibot/card.md","filePath":"src/harukibot/card.md"}'),r={name:"src/harukibot/card.md"};function l(i,e,t,n,s,h){return c(),d("div",null,[...e[0]||(e[0]=[a('<h1 id="卡牌与活动信息查询" tabindex="-1">卡牌与活动信息查询 <a class="header-anchor" href="#卡牌与活动信息查询" aria-label="Permalink to &quot;卡牌与活动信息查询&quot;">​</a></h1><blockquote><p>查卡面/查活动功能由 <a href="https://github.com/cYanosora" target="_blank" rel="noreferrer">Yozora</a> 编写，感谢贡献。</p></blockquote><h3 id="卡牌查询" tabindex="-1">卡牌查询 <a class="header-anchor" href="#卡牌查询" aria-label="Permalink to &quot;卡牌查询&quot;">​</a></h3><ul><li><code>查卡</code> / <code>查卡面</code> / <code>查询卡面</code> / <code>findcard [角色昵称]</code><br> 获取该角色所有卡牌。</li><li><code>查卡</code> / <code>查卡面</code> / <code>查询卡面</code> / <code>cardinfo [卡面ID]</code><br> 获取指定卡牌的详细信息。</li></ul><h3 id="活动查询" tabindex="-1">活动查询 <a class="header-anchor" href="#活动查询" aria-label="Permalink to &quot;活动查询&quot;">​</a></h3><ul><li><code>查活动</code> / <code>查询活动</code> / <code>event [活动ID]</code><br> 查看指定活动信息（省略 ID 则查看当前活动）。</li><li><code>查活动</code> / <code>查询活动</code> / <code>findevent [关键字]</code><br> 通过关键字筛选活动，返回活动概要图。</li></ul><h4 id="关键字使用示例" tabindex="-1">关键字使用示例 <a class="header-anchor" href="#关键字使用示例" aria-label="Permalink to &quot;关键字使用示例&quot;">​</a></h4><p><strong>单关键字</strong>：</p><ul><li><code>查活动 5v5</code> → 类型为 5v5 的活动</li><li><code>查活动 紫月</code> → 属性为紫月的活动</li><li><code>查活动 knd</code> → 出卡含宵崎奏的活动</li></ul><p><strong>多关键字</strong>：</p><ul><li><code>查活动 草 5v5</code> → 绿色 + 5v5 活动</li><li><code>查活动 knd 蓝</code> → 宵崎奏 + 蓝色活动</li><li><code>查活动 普活 紫月 knd</code> → 马拉松 + 紫月 + 含 knd 卡</li></ul><p><strong>特殊组合</strong>：</p><ul><li><code>查活动 25h</code> → 仅返回 25 箱活</li><li><code>查活动 25h 25miku</code> → 25 箱活且 VS 卡为初音未来</li><li><code>查活动 knd ick</code> → 同时含宵崎奏和晓山瑞希的混活</li></ul><h3 id="活动总览" tabindex="-1">活动总览 <a class="header-anchor" href="#活动总览" aria-label="Permalink to &quot;活动总览&quot;">​</a></h3><ul><li><code>活动图鉴</code> / <code>活动列表</code> / <code>活动总览</code> / <code>findevent all</code><br> 查看所有活动概要（图片较大，频道 Bot 可能无法使用）。</li></ul>',15)])])}const _=o(r,[["render",l]]);export{b as __pageData,_ as default};
