import{_ as e,c,o as l,ae as d}from"./chunks/framework.Cqi9LMMK.js";const k=JSON.parse('{"title":"需上传数据的功能","description":"","frontmatter":{},"headers":[],"relativePath":"src/sakurabot/rg/pjsk/pjsk_upload_required.md","filePath":"src/sakurabot/rg/pjsk/pjsk_upload_required.md"}'),t={name:"src/sakurabot/rg/pjsk/pjsk_upload_required.md"};function s(i,o,a,p,r,n){return l(),c("div",null,[...o[0]||(o[0]=[d('<h1 id="需上传数据的功能" tabindex="-1">需上传数据的功能 <a class="header-anchor" href="#需上传数据的功能" aria-label="Permalink to &quot;需上传数据的功能&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>以下功能需要先上传游戏数据才可使用。 请先访问上传页面获取教程：<a href="http://go.mikuware.top/" target="_blank" rel="noreferrer">点我进入上传数据页面（日/台服）</a></p></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p><strong>日服玩家简化上传</strong> 日服玩家可直接将引继信息复制给 Bot 实现上传（需要重新引继一次）。</p></div><h3 id="数据查询" tabindex="-1">数据查询 <a class="header-anchor" href="#数据查询" aria-label="Permalink to &quot;数据查询&quot;">​</a></h3><ul><li><code>rk</code>获取玩家排位信息。</li><li><code>b39</code>获取 Rating 分数最高的 39 首歌曲和总分（仅供参考）。</li><li><code>pjsk b30</code>获取 Rating 分数最高的 30 首歌曲和平均分（仅供参考）。</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>与 b39 的分数算法不同，可随意参考。</p></div><ul><li><p><code>pjsk进度</code> / <code>pjsk进度ex</code> / <code>pjsk进度apd</code>查询歌曲完成情况：</p><ul><li>默认：master 难度的 AP、FC、Clear、All 进度</li><li><code>ex</code>：expert 难度</li><li><code>apd</code>：append 难度<div class="info custom-block"><p class="custom-block-title">INFO</p><p>不支持其他难度查询。</p></div></li></ul></li><li><p><code>pjskdetail</code>获取个人详细信息，包括金币、水晶、家具练度、每日挑战进度等。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>包含敏感数据，如介意可私聊查询。</p></div></li><li><p><code>pjskcard</code>获取个人卡牌图鉴。可通过 <code>pjskcard + 角色花名</code> 查看单角色图鉴。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>已支持多服务器。</p></div></li><li><p><code>pjskevent</code> 获取个人 10 次最高排名的活动记录。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>当期需获得排名奖励才会被记录。</p></div></li></ul><h3 id="组卡推荐" tabindex="-1">组卡推荐 <a class="header-anchor" href="#组卡推荐" aria-label="Permalink to &quot;组卡推荐&quot;">​</a></h3><ul><li><p><code>组卡 队伍 属性</code>返回指定箱活中推荐使用的最佳卡组 top7，按活动点数排序。<strong>参数说明</strong>：</p><ul><li>队伍：<code>ln</code> / <code>mmj</code> / <code>vbs</code> / <code>ws</code> / <code>25</code> / <code>vs</code></li><li>属性：<code>绿</code> / <code>粉</code> / <code>橙</code> / <code>蓝</code> / <code>紫</code><strong>示例</strong>：<code>组卡 ln 橙</code></li></ul></li><li><p><code>活动组卡 [歌曲] [难度]</code>返回当前活动中推荐使用的最佳卡组 top7。<strong>默认值</strong>：</p><ul><li>歌曲：独りんぼエンヴィー</li><li>难度：expert <strong>示例</strong>：<code>活动组卡 龙 hard</code><div class="info custom-block"><p class="custom-block-title">INFO</p><p>World Link 活动时，第一个参数会被识别为对应角色。不输入参数则仅按加成计算。</p></div></li></ul></li><li><p><code>挑战组卡 角色 歌曲 难度</code>返回每日挑战中推荐使用的最佳卡组 top7，按分数排序。<strong>默认值</strong>：</p><ul><li>歌曲：独りんぼエンヴィー</li><li>难度：expert <strong>示例</strong>：<code>挑战组卡 ick 10th master</code></li></ul></li><li><p><code>最强组卡</code> 返回同色同队中分数最高的卡组 top7（锁定歌曲为独りんぼエンヴィー的 expert 难度），无需参数。</p></li></ul><h3 id="其他查询" tabindex="-1">其他查询 <a class="header-anchor" href="#其他查询" aria-label="Permalink to &quot;其他查询&quot;">​</a></h3><ul><li><code>查时间</code> 查询账号注册时间。</li></ul>',11)])])}const m=e(t,[["render",s]]);export{k as __pageData,m as default};
